// ROLLUP_NO_REPLACE 
 const statuts_associatifs = "{\"parsed\":{\"_path\":\"/statuts_associatifs\",\"_dir\":\"\",\"_draft\":false,\"_partial\":false,\"_locale\":\"\",\"_empty\":false,\"title\":\"StatutsAssociatifs\",\"description\":\"La Montgolfière\",\"body\":{\"type\":\"root\",\"children\":[{\"type\":\"element\",\"tag\":\"p\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"La Montgolfière\"}]},{\"type\":\"element\",\"tag\":\"h1\",\"props\":{\"id\":\"ecole-parentale-la-montgolfiere\"},\"children\":[{\"type\":\"text\",\"value\":\"ECOLE PARENTALE LA MONTGOLFIERE\"}]},{\"type\":\"element\",\"tag\":\"h1\",\"props\":{\"id\":\"statuts-associatifs\"},\"children\":[{\"type\":\"text\",\"value\":\"Statuts associatifs\"}]},{\"type\":\"element\",\"tag\":\"h2\",\"props\":{\"id\":\"titre-i-présentation-de-lassociation\"},\"children\":[{\"type\":\"text\",\"value\":\"TITRE I : Présentation de l’Association\"}]},{\"type\":\"element\",\"tag\":\"h3\",\"props\":{\"id\":\"article-1er-constitution-dénomination-siège-social-et-droit-applicable\"},\"children\":[{\"type\":\"element\",\"tag\":\"em\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"Article 1er :\"}]},{\"type\":\"text\",\"value\":\" Constitution, dénomination, siège social et droit applicable\"}]},{\"type\":\"element\",\"tag\":\"p\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"Le 1er avril 2023, il a été constitué par les membres fondateurs :\"}]},{\"type\":\"element\",\"tag\":\"p\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"CATTEAU Jenny, demeurant à Queyrac\"}]},{\"type\":\"element\",\"tag\":\"p\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"GURAL Kathlyn, demeurant à Vertheuil\"}]},{\"type\":\"element\",\"tag\":\"p\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"Une association régie par la loi du 1er juillet 1901 et le décret du 16 août 1901 ayant pour dénomination : « \"},{\"type\":\"element\",\"tag\":\"em\",\"props\":{},\"children\":[{\"type\":\"element\",\"tag\":\"strong\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"École La Montgolfière\"}]}]},{\"type\":\"text\",\"value\":\" ».\"}]},{\"type\":\"element\",\"tag\":\"p\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"Le siège social est fixé à Vertheuil Il pourra être modifié par décision du conseil d’administration.\"}]},{\"type\":\"element\",\"tag\":\"p\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"L'association est régie par la loi du 1er juillet 1901 et le décret du 16 août 1901.\"}]},{\"type\":\"element\",\"tag\":\"h3\",\"props\":{\"id\":\"article-2-objet\"},\"children\":[{\"type\":\"element\",\"tag\":\"em\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"Article 2\"}]},{\"type\":\"text\",\"value\":\" : Objet\"}]},{\"type\":\"element\",\"tag\":\"p\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"Cette association a pour objet :\"}]},{\"type\":\"element\",\"tag\":\"ul\",\"props\":{},\"children\":[{\"type\":\"element\",\"tag\":\"li\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"La gestion d'une école parentale participative à pédagogie active ;\"}]},{\"type\":\"element\",\"tag\":\"li\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"La mise en place et la gestion d’activités annexes : activités périscolaires et extrascolaires, stages de vacances, formations aux pédagogies actives, organisation d’événements éducatifs, culturels et pédagogiques (accompagnement de parents et de groupes, rencontres, manifestations, activités ludiques, sorties culturelles, etc.), dans et hors des locaux d’activités ;\"}]},{\"type\":\"element\",\"tag\":\"li\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"Toute activité complémentaire contribuant à la réalisation des deux points précédents.\"}]}]},{\"type\":\"element\",\"tag\":\"h3\",\"props\":{\"id\":\"article-3-moyens-daction\"},\"children\":[{\"type\":\"element\",\"tag\":\"em\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"Article 3\"}]},{\"type\":\"text\",\"value\":\" : Moyens d’action\"}]},{\"type\":\"element\",\"tag\":\"p\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"Dans le cadre de la réalisation de ses objectifs et sans que l’énumération qui suit ne soit limitative, l’association s’engage, en s’appuyant sur la participation bénévole de ses membres, à mettre en œuvre les moyens suivants :\"}]},{\"type\":\"element\",\"tag\":\"p\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"Pour accueillir les enfants à l’école :\"}]},{\"type\":\"element\",\"tag\":\"ul\",\"props\":{},\"children\":[{\"type\":\"element\",\"tag\":\"li\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"L’aménagement et la mise à disposition de locaux d’activités dédiés ;\"}]},{\"type\":\"element\",\"tag\":\"li\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"Le recrutement et l’emploi d’éducateur(trice)s et de personnels dédié(e)·s ;\"}]},{\"type\":\"element\",\"tag\":\"li\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"La mise en place d’un calendrier scolaire dédié.\"}]}]},{\"type\":\"element\",\"tag\":\"p\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"Pour favoriser la coopération :\"}]},{\"type\":\"element\",\"tag\":\"ul\",\"props\":{},\"children\":[{\"type\":\"element\",\"tag\":\"li\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"La mise en place d’instances de dialogue régulières parents\"}]},{\"type\":\"element\",\"tag\":\"li\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"Educateur(trice)s / enfants\"}]},{\"type\":\"element\",\"tag\":\"li\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"Educateur(trice)s ;\"}]},{\"type\":\"element\",\"tag\":\"li\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"Un fonctionnement participatif impliquant l’ensemble des parents ainsi que l’encouragement des parents à lancer des initiatives pour l’école.\"}]}]},{\"type\":\"element\",\"tag\":\"p\",\"props\":{},\"children\":[{\"type\":\"element\",\"tag\":\"code-inline\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"\\t\"}]},{\"type\":\"text\",\"value\":\"Pour favoriser l’accès de l’école à un plus large public :\"}]},{\"type\":\"element\",\"tag\":\"ul\",\"props\":{},\"children\":[{\"type\":\"element\",\"tag\":\"li\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"L’adoption d’une grille tarifaire indexée sur le nombre d'enfants pour les frais de scolarité ;\"}]},{\"type\":\"element\",\"tag\":\"li\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"La mise en place et la gestion d’activités annexes susceptibles de représenter des revenus supplémentaires pour l’association.\"}]}]},{\"type\":\"element\",\"tag\":\"p\",\"props\":{},\"children\":[{\"type\":\"element\",\"tag\":\"code-inline\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"\\t\"}]},{\"type\":\"text\",\"value\":\"Pour être un lieu-ressource autour de l’éducation :\"}]},{\"type\":\"element\",\"tag\":\"ul\",\"props\":{},\"children\":[{\"type\":\"element\",\"tag\":\"li\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"L’organisation d’événements relatifs à l’éducation au sein de l’école ;\"}]},{\"type\":\"element\",\"tag\":\"li\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"L’accueil en observation d’éducateur(trice)s, de familles et de toutes personnes susceptibles de jouer un rôle dans le développement de pratiques pédagogiques alternatives.\"}]}]},{\"type\":\"element\",\"tag\":\"h3\",\"props\":{\"id\":\"article-4-documents-constitutifs\"},\"children\":[{\"type\":\"element\",\"tag\":\"em\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"Article 4\"}]},{\"type\":\"text\",\"value\":\" : Documents constitutifs\"}]},{\"type\":\"element\",\"tag\":\"p\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"Les présents statuts sont accompagnés du règlement intérieur (RI), mis à disposition dans les locaux d’activité de l’association. Le RI de l’association est voté par l’assemblée générale. Il prévaut aux présents statuts. Ce RI est destiné à fixer les divers points non prévus par les statuts et à les préciser. Il est présenté à chaque nouvelle adhésion, chaque renouvellement annuel ou à chacune de ses modifications, à tou(te)s les adhérent(e)s.\"}]},{\"type\":\"element\",\"tag\":\"h2\",\"props\":{\"id\":\"titre-ii-composition-de-lassociation\"},\"children\":[{\"type\":\"text\",\"value\":\"TITRE II : Composition de l’Association\"}]},{\"type\":\"element\",\"tag\":\"h3\",\"props\":{\"id\":\"article-5-catégories-de-membres\"},\"children\":[{\"type\":\"element\",\"tag\":\"em\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"Article 5\"}]},{\"type\":\"text\",\"value\":\" : Catégories de membres\"}]},{\"type\":\"element\",\"tag\":\"p\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"L’Association se compose de :\"}]},{\"type\":\"element\",\"tag\":\"ul\",\"props\":{},\"children\":[{\"type\":\"element\",\"tag\":\"li\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"Membres actifs(ves). Sont membres actifs : les parents d’élèves scolarisés à l’école et/ou les salarié·e·s de l’école, à jour de leur cotisation. Ils ont le droit de vote en AG. Seuls les parents d’élève sont éligibles au CA.\"}]},{\"type\":\"element\",\"tag\":\"li\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"Membres fondateurs : Elles ont droit de vote en AG, sont membres de droit du conseil d’administration et sont dispensées du paiement de cotisation. Si elles sont salariées de l’Ecole La Mongolfière, elles ne peuvent être éligibles au bureau du CA\"}]},{\"type\":\"element\",\"tag\":\"li\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"Membres qualifiés : elles Ils sont proposés par le conseil d’administration et validées par l’assemblée générale pour une durée d’un an. Elles ils sont dispensé·e·s du paiement de cotisation. Elles Ils ont le droit de vote en AG et sont éligibles au CA.\"}]}]},{\"type\":\"element\",\"tag\":\"p\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"Les droits et devoirs des membres, au sein de l‘association, sont précisés dans le RI de l’association.\"}]},{\"type\":\"element\",\"tag\":\"p\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"L’association accueille également en son sein des bénévoles. Elles Ils ne sont pas membres mais bénéficient des diverses prestations de l’association (stages, formations, événements, etc.).\"}]},{\"type\":\"element\",\"tag\":\"h3\",\"props\":{\"id\":\"article-6-accession-au-titre-de-membre\"},\"children\":[{\"type\":\"element\",\"tag\":\"em\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"Article 6\"}]},{\"type\":\"text\",\"value\":\" : Accession au titre de membre\"}]},{\"type\":\"element\",\"tag\":\"p\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"Pour faire partie de l’association, il est nécessaire de :\"}]},{\"type\":\"element\",\"tag\":\"ul\",\"props\":{},\"children\":[{\"type\":\"element\",\"tag\":\"li\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"Prendre connaissance et adhérer aux présents statuts ainsi qu’au RI de l’association ;\"}]},{\"type\":\"element\",\"tag\":\"li\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"S’acquitter de la cotisation annuelle si nécessaire d’après l’article 5 ;\"}]},{\"type\":\"element\",\"tag\":\"li\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"Participer activement à titre bénévole aux activités de l’association.\"}]}]},{\"type\":\"element\",\"tag\":\"p\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"Les cotisations annuelles, dues par les membres de l’association, sont proposées par le conseil d’administration et validées par l’assemblée générale ordinaire. Elles permettent à l’association de financer des frais administratifs.\"}]},{\"type\":\"element\",\"tag\":\"p\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"Le conseil d’administration peut refuser des adhésions sur la base des présents statuts et avis motivé aux personnes intéressées.\"}]},{\"type\":\"element\",\"tag\":\"h3\",\"props\":{\"id\":\"article-7-perte-de-la-qualité-de-membre\"},\"children\":[{\"type\":\"element\",\"tag\":\"em\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"Article 7\"}]},{\"type\":\"text\",\"value\":\" : Perte de la qualité de membre\"}]},{\"type\":\"element\",\"tag\":\"p\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"La qualité de membre active ou de membre qualifiée se perd par :\"}]},{\"type\":\"element\",\"tag\":\"ul\",\"props\":{},\"children\":[{\"type\":\"element\",\"tag\":\"li\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"Le décès ;\"}]},{\"type\":\"element\",\"tag\":\"li\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"La démission par décision du membre ou le consentement mutuel, adressée par courrier au conseil d’administration ;\"}]},{\"type\":\"element\",\"tag\":\"li\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"La radiation prononcée par le conseil d’administration pour non-paiement de la cotisation après une mise en demeure préalable ;\"}]},{\"type\":\"element\",\"tag\":\"li\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"L'exclusion, prononcée par le conseil d’administration, pour infraction aux statuts ou au RI de l’association ou tout autre motif grave portant préjudice aux intérêts moraux et/ou matériels de l'association, et de ses membres.\"}]}]},{\"type\":\"element\",\"tag\":\"p\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"Nul ne peut se voir exclu de l'association sans avoir pu défendre ses droits devant le CA ou pour des motifs non légitimes.\"}]},{\"type\":\"element\",\"tag\":\"h2\",\"props\":{\"id\":\"titre-iii-lassemblée-générale\"},\"children\":[{\"type\":\"text\",\"value\":\"TITRE III : L’assemblée générale\"}]},{\"type\":\"element\",\"tag\":\"h3\",\"props\":{\"id\":\"article-8-composition-de-lassemblée-générale\"},\"children\":[{\"type\":\"element\",\"tag\":\"em\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"Article 8\"}]},{\"type\":\"text\",\"value\":\" : Composition de l'assemblée générale\"}]},{\"type\":\"element\",\"tag\":\"p\",\"props\":{},\"children\":[{\"type\":\"element\",\"tag\":\"code-inline\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\" \"}]},{\"type\":\"text\",\"value\":\"L'assemblée générale (AG) comprend les membres et est ouverte à toutes.\"}]},{\"type\":\"element\",\"tag\":\"p\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"Toutes les membres de l’association sont appelés à délibérer et à voter durant les AG.\"}]},{\"type\":\"element\",\"tag\":\"h3\",\"props\":{\"id\":\"article-9-convocation-ordre-du-jour-et-modalités-de-réunion-de-lassemblée-générale-ordinaire\"},\"children\":[{\"type\":\"element\",\"tag\":\"em\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"Article 9\"}]},{\"type\":\"text\",\"value\":\" : Convocation, ordre du jour et modalités de réunion de l’assemblée générale ordinaire\"}]},{\"type\":\"element\",\"tag\":\"p\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"L’assemblée générale ordinaire (AGO) se réunit au moins une fois par an sur convocation du bureau ou du conseil d’administration ou à la demande d'un tiers des membres.\"}]},{\"type\":\"element\",\"tag\":\"p\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"Le délai de convocation des membres à l’AGO est de quinze jours.\"}]},{\"type\":\"element\",\"tag\":\"p\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"Le quorum est de la moitié des membres. Si ce quorum n'est pas atteint, l’AGO est à nouveau convoquée dans l’intervalle de quinze jours à deux mois suivant la date de la première convocation. Si le quorum n’est à nouveau pas atteint, l’AGO peut par conséquent délibérer quel que soit le nombre de membres.\"}]},{\"type\":\"element\",\"tag\":\"p\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"L'ordre du jour est dressé par l’organe convoquant. Toutes les membres de l’association ont la possibilité d’ajouter à l’ordre du jour établi, toute question relative aux activités de l’association, en lien avec les attributions fixées dans l’article 11, et ce jusqu’à une semaine avant la date fixée pour l’AGO.\"}]},{\"type\":\"element\",\"tag\":\"p\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"Ne peuvent être traitées, lors de l’AGO, que les questions soumises à l’ordre du jour. Seules sont valables les résolutions prises par l’AG sur les points inscrits à l'ordre du jour.\"}]},{\"type\":\"element\",\"tag\":\"h3\",\"props\":{\"id\":\"article-10-les-délibérations-et-le-vote\"},\"children\":[{\"type\":\"element\",\"tag\":\"em\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"Article 10\"}]},{\"type\":\"text\",\"value\":\" : Les délibérations et le vote\"}]},{\"type\":\"element\",\"tag\":\"p\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"Chaque membre dispose d'une seule voix à l'AG. Un membre se retire du vote lorsque la délibération concerne la conclusion de tout acte entre lui et l'association.\"}]},{\"type\":\"element\",\"tag\":\"p\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"Les décisions de l’AG sont prises à la majorité absolue des voix présentes ou représentées. Elles sont prises à main levée. Cependant, un vote à bulletin secret est mis en place si une seul·e des membres présentes le demande.\"}]},{\"type\":\"element\",\"tag\":\"p\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"Les membres peuvent se faire représenter par un autre membre par procuration. Le mandat signé du membre absent devra être présenté au secrétaire de l’AG en début de séance. Un membre présent ne peut représenter qu’un seul membre absent.\"}]},{\"type\":\"element\",\"tag\":\"p\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"Afin de garantir le fonctionnement démocratique de l’association, les délibérations sont constatées par procès-verbaux, signés de deux personnes du conseil d’administration.\"}]},{\"type\":\"element\",\"tag\":\"h3\",\"props\":{\"id\":\"article-11-les-attributions-des-assemblées-générales-ordinaires\"},\"children\":[{\"type\":\"element\",\"tag\":\"em\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"Article 11\"}]},{\"type\":\"text\",\"value\":\" : Les attributions des assemblées générales ordinaires\"}]},{\"type\":\"element\",\"tag\":\"p\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"Le.la présidente, assistée du conseil d’administration, préside l’assemblée générale.\"}]},{\"type\":\"element\",\"tag\":\"p\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"L’AGO délibère et statue sur les points de l'ordre du jour.\"}]},{\"type\":\"element\",\"tag\":\"p\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"Elle entend les rapports moraux et financier\"}]},{\"type\":\"element\",\"tag\":\"p\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"Elle se prononce sur les différents rapports et les comptes de l'exercice clos.\"}]},{\"type\":\"element\",\"tag\":\"p\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"Elle entend le projet de budget pour l'exercice suivant et le vote.\"}]},{\"type\":\"element\",\"tag\":\"p\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"Elle délibère sur les orientations à venir et la politique générale de l'association.\"}]},{\"type\":\"element\",\"tag\":\"p\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"Elle élit les membres du conseil d'administration.\"}]},{\"type\":\"element\",\"tag\":\"p\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"Elle vote le montant des cotisations annuelles.\"}]},{\"type\":\"element\",\"tag\":\"h3\",\"props\":{\"id\":\"article-12-lassemblée-générale-extraordinaire-convocation-et-attributions\"},\"children\":[{\"type\":\"element\",\"tag\":\"em\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"Article 12\"}]},{\"type\":\"text\",\"value\":\" : L’assemblée générale extraordinaire - Convocation et attributions\"}]},{\"type\":\"element\",\"tag\":\"p\",\"props\":{},\"children\":[{\"type\":\"element\",\"tag\":\"code-inline\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"\\t\"}]},{\"type\":\"text\",\"value\":\"Une assemblée générale extraordinaire (AGE) peut être réunie en cas de circonstances exceptionnelles, à la demande du CA ou du tiers au moins des membres.\"}]},{\"type\":\"element\",\"tag\":\"p\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"Le délai de convocation des membres à l’AGE est de quinze jours. Le quorum est de la moitié des membres. Si ce quorum n'est pas atteint, l'AGE est à nouveau convoquée dans l’intervalle de quinze jours à deux mois suivant la date de la première convocation. Si le quorum n’est à nouveau pas atteint, l’AGE peut délibérer par conséquent quel que soit le nombre de membres présents ou représentés.\"}]},{\"type\":\"element\",\"tag\":\"p\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"L'AGE a compétence pour voter la modification des statuts et du RI, décider la dissolution de l'association et l'attribution des biens de l'association, sa fusion avec toute autre association poursuivant un but analogue, ou son affiliation à une union d'associations.\"}]},{\"type\":\"element\",\"tag\":\"p\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"Elle a également compétence pour révoquer un ou des membre(s) du conseil d’administration et procéder à l’élection de nouveaux membres.\"}]},{\"type\":\"element\",\"tag\":\"h2\",\"props\":{\"id\":\"titre-iv-le-conseil-dadministration\"},\"children\":[{\"type\":\"element\",\"tag\":\"code-inline\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\" \"}]},{\"type\":\"text\",\"value\":\"TITRE IV : Le conseil d’administration\"}]},{\"type\":\"element\",\"tag\":\"h3\",\"props\":{\"id\":\"article-13-composition-du-conseil-dadministration\"},\"children\":[{\"type\":\"element\",\"tag\":\"em\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"Article 13\"}]},{\"type\":\"text\",\"value\":\" : Composition du conseil d'administration\"}]},{\"type\":\"element\",\"tag\":\"p\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"L’association est administrée par un conseil d'administration (CA) composé des membres fondatrices, et de trois à huit autres membres actif. Es ou qualifié.es. Les membres élus sortants sont rééligibles.\"}]},{\"type\":\"element\",\"tag\":\"p\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"Le conseil d’administration doit être composée de parents d’élève à plus de cinquante pour cent.\"}]},{\"type\":\"element\",\"tag\":\"p\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"Le CA élit parmi ses membres un représentant légal ;\"}]},{\"type\":\"element\",\"tag\":\"p\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"Le CA a la charge de veiller à la mise en œuvre des délibérations du CA et de l’AG, d’assumer la gestion courante de l’association, dans le cadre des orientations arrêtées, de veiller à son bon fonctionnement statutaire et au respect de la réglementation.\"}]},{\"type\":\"element\",\"tag\":\"p\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"Toute personne du CA qui souhaite démissionner doit en informer par voie écrite l’ensemble des autres membres du CA et laisser un délai suffisant pour son remplacement.\"}]},{\"type\":\"element\",\"tag\":\"p\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"S’il l’estime nécessaire, le CA peut élire un bureau dont le nombre de membres et les missions précises sont fixés par le CA.\"}]},{\"type\":\"element\",\"tag\":\"h2\",\"props\":{\"id\":\"titre-v-ressources-de-lassociation\"},\"children\":[{\"type\":\"text\",\"value\":\"TITRE V : Ressources de l’association\"}]},{\"type\":\"element\",\"tag\":\"p\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"Les ressources de l’association se composent :\"}]},{\"type\":\"element\",\"tag\":\"ul\",\"props\":{},\"children\":[{\"type\":\"element\",\"tag\":\"li\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"Des frais d’inscription et des frais de scolarité ;\"}]},{\"type\":\"element\",\"tag\":\"li\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"Du revenu de la vente de produits ou de services ;\"}]},{\"type\":\"element\",\"tag\":\"li\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"Du montant des cotisations, des participations aux activités de l’association ;\"}]},{\"type\":\"element\",\"tag\":\"li\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"Du produit de mécénat d’entreprises, de dons, de legs ;\"}]},{\"type\":\"element\",\"tag\":\"li\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"De subventions de l’Etat et de toutes collectivités territoriales ;\"}]},{\"type\":\"element\",\"tag\":\"li\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"De toutes autres ressources ou subventions qui ne seraient pas contraires aux lois en vigueur.\"}]}]},{\"type\":\"element\",\"tag\":\"h2\",\"props\":{\"id\":\"titre-vi-ri\"},\"children\":[{\"type\":\"text\",\"value\":\"TITRE VI : RI\"}]},{\"type\":\"element\",\"tag\":\"p\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"Il existe un règlement intérieur pour compléter les présents statuts. Il doit être validé par le conseil d’administration qui le présente pour validation à l’assemblée générale.\"}]},{\"type\":\"element\",\"tag\":\"h2\",\"props\":{\"id\":\"titre-vii-dissolution\"},\"children\":[{\"type\":\"text\",\"value\":\"TITRE VII : Dissolution\"}]},{\"type\":\"element\",\"tag\":\"p\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"L'association ne peut être dissoute que par une AGE réunie à cet effet, sur proposition du CA ou du tiers des membres. Une convocation accompagnée d'une date, d'un lieu, d'une heure et d'un ordre du jour à point unique est adressée à toutes les membres au moins quinze jours avant la réunion de l’AGE.\"}]},{\"type\":\"element\",\"tag\":\"p\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"En cas de dissolution volontaire, statutaire ou judiciaire, l’AGE désigne un ou plusieurs liquidateurs et l'actif, s'il y a lieu, est dévolu conformément à l'article 9 de la loi du 1er juillet 1901 et au décret du 16 août 1901.\"}]},{\"type\":\"element\",\"tag\":\"p\",\"props\":{},\"children\":[{\"type\":\"text\",\"value\":\"Les membres de l’association ne peuvent se voir attribuer une part quelconque des biens de l’association. L’actif net subsistant sera attribué à une ou plusieurs structures poursuivant des buts similaires désignés par le bureau.\\r\\nAssociation Loi 1901 déclarée le ,1er avril 2023 au 4 lotissement « Les Jonquilles » 33180 VERTHEUIL n siret\"}]}],\"toc\":{\"title\":\"\",\"searchDepth\":2,\"depth\":2,\"links\":[{\"id\":\"titre-i-présentation-de-lassociation\",\"depth\":2,\"text\":\"TITRE I : Présentation de l’Association\",\"children\":[{\"id\":\"article-1er-constitution-dénomination-siège-social-et-droit-applicable\",\"depth\":3,\"text\":\"Article 1er : Constitution, dénomination, siège social et droit applicable\"},{\"id\":\"article-2-objet\",\"depth\":3,\"text\":\"Article 2 : Objet\"},{\"id\":\"article-3-moyens-daction\",\"depth\":3,\"text\":\"Article 3 : Moyens d’action\"},{\"id\":\"article-4-documents-constitutifs\",\"depth\":3,\"text\":\"Article 4 : Documents constitutifs\"}]},{\"id\":\"titre-ii-composition-de-lassociation\",\"depth\":2,\"text\":\"TITRE II : Composition de l’Association\",\"children\":[{\"id\":\"article-5-catégories-de-membres\",\"depth\":3,\"text\":\"Article 5 : Catégories de membres\"},{\"id\":\"article-6-accession-au-titre-de-membre\",\"depth\":3,\"text\":\"Article 6 : Accession au titre de membre\"},{\"id\":\"article-7-perte-de-la-qualité-de-membre\",\"depth\":3,\"text\":\"Article 7 : Perte de la qualité de membre\"}]},{\"id\":\"titre-iii-lassemblée-générale\",\"depth\":2,\"text\":\"TITRE III : L’assemblée générale\",\"children\":[{\"id\":\"article-8-composition-de-lassemblée-générale\",\"depth\":3,\"text\":\"Article 8 : Composition de l'assemblée générale\"},{\"id\":\"article-9-convocation-ordre-du-jour-et-modalités-de-réunion-de-lassemblée-générale-ordinaire\",\"depth\":3,\"text\":\"Article 9 : Convocation, ordre du jour et modalités de réunion de l’assemblée générale ordinaire\"},{\"id\":\"article-10-les-délibérations-et-le-vote\",\"depth\":3,\"text\":\"Article 10 : Les délibérations et le vote\"},{\"id\":\"article-11-les-attributions-des-assemblées-générales-ordinaires\",\"depth\":3,\"text\":\"Article 11 : Les attributions des assemblées générales ordinaires\"},{\"id\":\"article-12-lassemblée-générale-extraordinaire-convocation-et-attributions\",\"depth\":3,\"text\":\"Article 12 : L’assemblée générale extraordinaire - Convocation et attributions\"}]},{\"id\":\"titre-iv-le-conseil-dadministration\",\"depth\":2,\"text\":\" TITRE IV : Le conseil d’administration\",\"children\":[{\"id\":\"article-13-composition-du-conseil-dadministration\",\"depth\":3,\"text\":\"Article 13 : Composition du conseil d'administration\"}]},{\"id\":\"titre-v-ressources-de-lassociation\",\"depth\":2,\"text\":\"TITRE V : Ressources de l’association\"},{\"id\":\"titre-vi-ri\",\"depth\":2,\"text\":\"TITRE VI : RI\"},{\"id\":\"titre-vii-dissolution\",\"depth\":2,\"text\":\"TITRE VII : Dissolution\"}]}},\"_type\":\"markdown\",\"_id\":\"content:statuts_associatifs.md\",\"_source\":\"content\",\"_file\":\"statuts_associatifs.md\",\"_extension\":\"md\"},\"hash\":\"CKrzKXR5La\"}";

